const postContainer=document.getElementById("postContainer"),searchForm=document.getElementById("searchForm"),postIdInput=document.getElementById("postId");function fetchPosts(t){fetch("https://jsonplaceholder.typicode.com/posts/"+t).then(t=>{if(t.ok)return t.json();throw new Error(t.status)}).then(t=>{postContainer.innerHTML=`
      <div class="post">
        <h2>${t.title}</h2>
        <p>${t.body}</p>
        <div id="commentsContainer">
          <button id="commentsButton" data-post="${t.id}">Load Comments</button>
        </div>
      </div>
      `}).catch(t=>{console.log(t)})}function fetchComments(t){fetch("https://jsonplaceholder.typicode.com/comments?postId="+t).then(t=>{if(t.ok)return t.json();throw new Error(t.status)}).then(t=>{const n=document.getElementById("commentsContainer");n.innerHTML="<h2>Comments</h2>",t.forEach(t=>{var e=document.createElement("div");e.innerHTML=`
        <h3>${t.name} (${t.email})</h3>
        <p>${t.body}</p>
        `,n.appendChild(e)})})}searchForm.addEventListener("submit",t=>{t.preventDefault();t=postIdInput.value;1<=t&&t<=100&&fetchPosts(t)}),postContainer.addEventListener("click",t=>{t=t.target;t.matches("#commentsButton")&&fetchComments(t.dataset.post)});